
public class simplejava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int a = 25;
	        int b = 70;

	        if (a > b) {
	            System.out.println(a + " is Big");
	        } else {
	            System.out.println(b + " is Big");
	        }

	        int ans = a > b ? a : b;
	        System.out.println(ans + "is big");//20
	    }
	}


